import { Outlet, Link } from 'react-router-dom';
import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext'; // Importa el contexto del carrito
import Cart from './Cart';

const Layout = () => {

    const { cart } = useContext(CartContext); // Obtiene el carrito del contexto
    const [isOpen, setIsOpen] = useState(false); // Estado para controlar el desplegable

    // Función para alternar el estado del desplegable
    const toggleDropdown = () => {
      setIsOpen(!isOpen);
    };

    // Calcular el total de la compra
    const total = cart.reduce((acc, product) => acc + product.price, 0);




    // Estilos en línea
    const styles = {
        container: {
            padding: '20px',
            maxWidth: '800px',
            margin: '0 auto',
        },
        nav: {
            backgroundColor: '#007bff',
            padding: '10px',
            borderRadius: '5px',
        },
        navList: {
            listStyleType: 'none',
            padding: 0,
            margin: 0,
            display: 'flex',
            justifyContent: 'space-around',
        },
        navItem: {
            margin: '0 15px',
        },
        navLink: {
            color: '#fff',
            textDecoration: 'none',
            fontWeight: 'bold',
            transition: 'color 0.3s',
        },
        navLinkHover: {
            color: '#ffc107',
        },
        logo: {
            fontSize: '24px',
            fontWeight: 'bold',
          },
          cartButton: {
            position: 'relative', // Necesario para el posicionamiento del desplegable
            padding: '10px 15px',
            backgroundColor: '#fff',
            color: '#007bff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            textDecoration: 'none',
            transition: 'background-color 0.3s',
          },
          cartDropdown: {
            position: 'absolute',
            top: '100%', // Se muestra justo debajo del botón
            right: 0,
            backgroundColor: '#fff',
            boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
            borderRadius: '5px',
            zIndex: 1,
            width: '200px',
            display: isOpen ? 'block' : 'none', // Controla la visibilidad del desplegable
          },
          dropdownItem: {
            padding: '10px',
            borderBottom: '1px solid #eee',
          },
          total: {
            padding: '10px',
            fontWeight: 'bold',
          },
    };

    return (
        <div style={styles.container}>
            <nav style={styles.nav}>
                <ul style={styles.navList}>
                    <li style={styles.navItem}>
                        <Link to="/" style={styles.navLink}>Home</Link>
                    </li>
                    <li style={styles.navItem}>
                        <Link to="/productos" style={styles.navLink}>Productos</Link>
                    </li>
                    <li style={styles.navItem}>
                    <div>
        <button style={styles.cartButton} onClick={toggleDropdown}>
          Carrito ({cart.length})
        </button>
        {isOpen && (
          <div style={styles.cartDropdown}>
            {cart.length === 0 ? (
              <div style={styles.dropdownItem}>El carrito está vacío.</div>
            ) : (
              cart.map((product) => (
                <div key={product.id} style={styles.dropdownItem}>
                  {product.title} - ${product.price}
                </div>
              ))
            )}
            <div style={styles.total}>Total: ${total}</div>
            <Link to="/carrito" style={styles.dropdownItem}>Ver Carrito</Link>
          </div>
        )}
      </div>
                    </li>
                </ul>
            </nav>
            <Outlet />
        </div>
    );
};

export default Layout;
